import Vue from 'vue'
import {createStore} from 'vuex'



  const store = createStore({
    actions: {},
    mutations: {},
    state: {
        books:
            [
                {
                    "id": "1",
                    "title": "Утро под Катовице",
                    "genre": "horror",
                    "author": "Tim Bubel",
                    "page": "35"
                },
                {
                    "id": "2",
                    "title": "Князь Никто",
                    "genre": "horror",
                    "author": "Ron Alket",
                    "page": "465"
                },
                {
                    "id": "3",
                    "title": "Ёшкин Кот",
                    "genre": "children",
                    "author": "Arnolr Rhimes",
                    "page": "15"
                },
                {
                    "id": "4",
                    "title": "Путишествие к центру земли",
                    "genre": "adventure",
                    "author": "Ron Moiseeich",
                    "page": "465"
                }
            ],
    },
    getters:{
        allBooks: state =>state.books
        },
        childrenBooks(state){
            return state.books
        },
        adventureBooks(state){
            return state.books
        },
        horrorbooks(state){
            return state.books
        }

    },

)
Vue.use(store)
