import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
    actions: {},
    mutations: {},
    state: {
        books:
            [
                {
                    "title": "Утро под Катовице",
                    "genre": "horror",
                    "author": "Tim Bubel",
                    "page": "35"
                },
                {
                    "title": "Князь Никто",
                    "genre": "horror",
                    "author": "Ron Alket",
                    "page": "465"
                },
                {
                    "title": "Ёшкин Кот",
                    "genre": "children",
                    "author": "Arnolr Rhimes",
                    "page": "15"
                },
                {
                    "title": "Путишествие к центру земли",
                    "genre": "adventure",
                    "author": "Ron Moiseeich",
                    "page": "465"
                }
            ],
    },
    getters:{
        allBooks(state){
            return state.books
        },
        childrenBooks(state){
            return state.books
        },
        adventureBooks(state){
            return state.books
        },
        horrorbooks(state){
            return state.books
        }

    },
    modules:{},
    }
)
