import { axiosInstance } from "@/services/api";
// import axios from "axios";

export default {
    getAllBooks({commit}){
       return axiosInstance.get('books', {
       })
           .then((resp) => {
               commit('setAllBooks', resp.data.data)
            })
           .catch((err) => console.log(err));
    },
    createNewBook({commit, dispatch }, newBook) {
        return axiosInstance.post('books', newBook)
            .then((resp) => {
                commit('setAllBooks')
                dispatch('getAllBooks')
                })
    },
    updateBooks({commit}, newBook){
        commit('initState', newBook)
    }
}
