import Vue from 'vue'
import Vuex from 'vuex'


Vue.use(Vuex)

export default new Vuex.Store({

    state: {
        books: [],
    },
    getters:{
        allBooks: state =>state.books,
        selected: state =>state.genre,
    },

    mutations: {
        setNewBook(state, newBook){
            state.books.push(newBook)
        },
        initState(state, newBooks){
            state.books = newBooks;
        }
    },
    actions: {
        createNewBook({commit}, newBook){
            commit('setNewBook', newBook)
        },
        updateBooks({commit}, newBook){
            commit('initState', newBook)
        }
    },

})
