import { axiosInstance } from "@/services/api";
import axios from "axios";

export default {
    getAllBooks({commit}, ){
       return  axiosInstance.get('/books')
            .then((resp) => {
                this.books = resp.data.data;
            }).catch((err) => console.log(err));
    },
    createNewBook({commit}, newBook){
        commit('setNewBook', newBook)
    },
    updateBooks({commit}, newBook){
        commit('initState', newBook)
    }
}
